import React, { Component } from 'react';
import classnames from 'classnames';
import ReactSVG from 'react-svg';

import { ICONS } from '../../../config/constants';
import { ActionNotification } from '../../';
import { getErrorLocalized } from '../../../utils/errors';

export const FieldContent = ({
	label = '',
	valid = false,
	hasValue = false,
	focused = false,
	children,
	hideUnderline = false,
	contentClassName = '',
	hideCheck = false,
	outlineClassName = ''
}) => {
	return (
		<div className={classnames('field-content')}>
			{label && <div className="field-label">{label}</div>}
			<div
				className={classnames(
					'field-children',
					{ valid, custom: hideUnderline },
					contentClassName
				)}
			>
				{children}
				{!hideCheck &&
					valid &&
						hasValue && (
							<ReactSVG path={ICONS.BLACK_CHECK} wrapperClassName="field-valid" />
					)}
			</div>
			{!hideUnderline && (
				<span
					className={classnames('field-content-outline', outlineClassName, {
						focused
					})}
				/>
			)}
		</div>
	);
};

export const FieldError = ({ error, displayError, className }) => (
	<div
		className={classnames('field-error-content', className, {
			'field-error-hidden': !displayError
		})}
	>
		{error && (
			<img src={ICONS.RED_WARNING} className="field-error-icon" alt="error" />
		)}
		{error && (
			<span className="field-error-text">{getErrorLocalized(error)}</span>
		)}
	</div>
);

class FieldWrapper extends Component {
	render() {
		const {
			children,
			label,
			input: { value },
			meta: { active = false, error = '', touched = false, invalid = false },
			focused = false,
			fullWidth = false,
			visited = false,
			hideUnderline = false,
			className = '',
			onClick = () => {},
			notification,
			hideCheck = false,
			outlineClassName = ''
		} = this.props;

		const displayError = !(active || focused) && (visited || touched) && error;
		const hasValue = value || value === false;
		return (
			<div
				className={classnames('field-wrapper', className, {
					error: displayError,
					inline: !fullWidth,
					'with-notification': !!notification,
					'field-valid': !invalid
				})}
			>
				<FieldContent
					label={label}
					valid={!invalid}
					hasValue={hasValue}
					focused={active || focused}
					hideUnderline={hideUnderline}
					hideCheck={hideCheck}
					outlineClassName={outlineClassName}
					onClick={onClick}
				>
					{children}
					{notification &&
						typeof notification === 'object' && (
							<ActionNotification
								{...notification}
								className="pr-0 pl-0 no_bottom"
								showActionText={true}
							/>
						)}
				</FieldContent>
				<FieldError displayError={displayError} error={error} />
			</div>
		);
	}
}

FieldWrapper.defaultProps = {
	meta: {},
	input: {
		value: ''
	}
};

export default FieldWrapper;
