import React from 'react';
import classnames from 'classnames';
import mathjs from 'mathjs';
import { isMobile } from 'react-device-detect';

import STRINGS from '../../config/localizedStrings';

import { CurrencyBall } from '../../components';
import { BLOCKTRAIL_ENDPOINT, ETHEREUM_ENDPOINT, BITCOINCOM_ENDPOINT, BASE_CURRENCY, CURRENCY_PRICE_FORMAT, DEFAULT_COIN_DATA } from '../../config/constants';
import { formatTimestamp, isBlockchainTx } from '../../utils/utils';
import { formatToCurrency } from '../../utils/currency';

const calculateFeeAmount = (
	fee = 0,
	quick = false,
	price = 1,
	size = 0,
	side = ''
) => {
	if (!fee || fee <= 0) {
		return STRINGS.NO_FEE;
	}
	let feeAmount = 0;
	if (side === 'buy') {
		feeAmount = mathjs
			.chain(size)
			.multiply(fee)
			.divide(100)
			.done();
	} else if (side === 'sell') {
		const amount = calculateAmount(quick, price, size);
		feeAmount = mathjs
			.chain(amount)
			.multiply(fee)
			.divide(100)
			.done();
	}
	return feeAmount;
};

const calculateAmount = (isQuick = false, price, size) => {
	if (isQuick) {
		return price;
	}
	const amount = mathjs
		.chain(price)
		.multiply(size)
		.done();
	return amount;
};

const calculatePrice = (isQuick = false, price, size) => {
	if (isQuick) {
		const amount = mathjs
			.chain(price)
			.divide(size)
			.done();
		return amount;
	}
	return price;
};

export const generateTradeHeaders = (symbol, pairs, coins) => {
	return [
		{
			label: STRINGS.PAIR,
			key: 'pair',
			exportToCsv: ({ symbol }) => symbol.toUpperCase(),
			renderCell: ({ symbol }, key, index) => {
				return (
					<td key={index} className="text-uppercase">
						{symbol}
					</td>
				);
			}
		},
		{
			label: STRINGS.TYPE,
			key: 'side',
			exportToCsv: ({ side = '' }) => side,
			renderCell: ({ side = '' }, key, index) => {
				return (
					<td key={index} className={classnames('cell_box-type')}>
						<div className={classnames(side)}>{STRINGS.SIDES_VALUES[side]}</div>
					</td>
				);
			}
		},
		{
			label: STRINGS.SIZE,
			key: 'size',
			exportToCsv: ({ size = 0, ...data }) => {
				if (pairs[data.symbol]) {
					const symbol = pairs[data.symbol].pair_base;
					const { min, ...rest } = coins[symbol || BASE_CURRENCY] || DEFAULT_COIN_DATA;
					const shortName = rest.symbol.toUpperCase();
					return STRINGS.formatString(
						CURRENCY_PRICE_FORMAT,
						formatToCurrency(size, min),
						shortName
					);
				}
			},
			renderCell: ({ size = 0, ...data }, key, index) => {
				if (pairs[data.symbol]) {
					const symbol = pairs[data.symbol].pair_base;
