import React from 'react';
import { InputNumber, Input, DatePicker, Select } from 'antd';
import moment from 'moment';
import { formatTimestampFarsi, DATETIME_FORMAT_FA } from '../../utils/date';
import './index.css';

const dateFormat = 'YYYY/MM/DD';

export const renderInputField = ({
	input,
	label,
	type,
	meta: { touched, error, warning },
	prefix,
	placeholder,
	disabled = false
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<Input
				placeholder={placeholder}
				prefix={prefix}
				{...input}
				type={type}
				disabled={disabled}
			/>
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);

export const renderNumberField = ({
	input,
	label,
	meta: { touched, error, warning },
	...rest
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<InputNumber {...rest} {...input} />
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);

export const renderSelectField = ({
	input,
	options,
	label,
	meta: { touched, error, warning },
	disabled = false
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<Select {...input} placeholder={label} disabled={disabled}>
				{options.map((option, index) => (
					<Select.Option value={option.value || option} key={index}>
						{option.label || option}
					</Select.Option>
				))}
			</Select>
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);

export const renderDateField = ({
	input,
	label,
	placeholder,
	meta: { touched, error, warning },
	disabled = false
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<DatePicker
				defaultValue={moment(input.value || new Date(), dateFormat)}
				onChange={input.onChange}
				format={dateFormat}
				disabled={disabled}
				placeholder={placeholder}
			/>
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);

export const renderFarsiDateField = ({
	input,
	label,
	placeholder,
	meta: { touched, error, warning },
	disabled = false
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<div className="date-wrapper-farsi">
				<div className="date-wrapper-farsi-value">
					{formatTimestampFarsi(input.value || new Date(), DATETIME_FORMAT_FA)}
				</div>
				<DatePicker
					className="date-wrapper-farsi-selector"
					defaultValue={moment(input.value || new Date(), dateFormat)}
					onChange={input.onChange}
					format={dateFormat}
					disabled={disabled}
					placeholder={placeholder}
				/>
			</div>
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);

export const renderRangeField = ({
	input,
	label,
	meta: { touched, error, warning },
	disabled = false
}) => (
	<div className="input_field">
		{label && <label>{label}</label>}
		<div>
			<DatePicker.RangePicker
				onChange={input.onChange}
				disabled={disabled}
				defaultValue={[
					moment(input.value[0], dateFormat),
					moment(input.value[1], dateFormat)
				]}
				format={dateFormat}
			/>
			{touched &&
				((error && <span className="red-text">{error}</span>) ||
					(warning && <span className="red-text">{warning}</span>))}
		</div>
	</div>
);
